mapper.select:
SELECT * FROM 
ejemplo_java_1.Usuarios

	id=#{id,jdbcType=INTEGER}
	 AND alias=#{alias,jdbcType=VARCHAR}
	 AND correo=#{correo,jdbcType=VARCHAR}
	AND contrasenya=PASSWORD(#{contrasenya,jdbcType=VARCHAR})

mapper.update:
UPDATE ejemplo_java_1.Usuarios
alias=#{alias,jdbcType=VARCHAR}, correo=#{correo,jdbcType=VARCHAR}, contrasenya=#{contrasenya,jdbcType=VARCHAR}, acceso=#{acceso,jdbcType=TIMESTAMP}, modificacion=#{modificacion,jdbcType=TIMESTAMP}
where id=#{id,jdbcType=INTEGER}

mapper.delete:
DELETE FROM 
ejemplo_java_1.Usuarios  

	id=#{id,jdbcType=INTEGER}
	 AND alias=#{alias,jdbcType=VARCHAR}
	 AND correo=#{correo,jdbcType=VARCHAR}
